        var topRx = p['top-rx'] || serie.bars['top-rx'];
        var topRy = p['top-ry'] || serie.bars['top-ry'];
        var bottomRx = p['bottom-rx'] || serie.bars['bottom-rx'];
        var bottomRy = p['bottom-ry'] || serie.bars['bottom-ry'];

